package models

type InsertDIDDocument struct {
	JsonRPC string       `json:"jsonrpc" example:"2.0" description:"" `
	Method  string       `json:"method" example:"insertDidDocument" description:""`
	Params  []DIDRParams `json:"params" `
	Id      int          `json:"id" example:"1" description:""`
}

type InsertIssuer struct {
	JsonRPC string      `json:"jsonrpc" example:"2.0" description:"" `
	Method  string      `json:"method" example:"insertDidDocument" description:""`
	Params  []TIRParams `json:"params" `
	Id      int         `json:"id" example:"1" description:""`
}

type DIDRParams struct {
	From               string `json:"from" example:"0x416e6e6162656c2e4c65652e452d412d506f652e" description:"" `
	Identifier         string `json:"identifier" example:"0x6469643a656273693a434245736e5445445434764d714a73396b38576759644e757945796b6a57706276645a4d6363596456456e4e" description:"" `
	HashAlgorithmId    int    `json:"hashAlgorithmId" example:"0" description:"" `
	HashValue          string `json:"hashValue" example:"0xf0fcfaf89af366f278900538f074eba8469408b1c54451e4ff4660a379e7e8f5" description:"" `
	DidVersionInfo     string `json:"didVersionInfo" example:"" description:"" `
	TimestampData      string `json:"timestampData" example:"0x7b2264617461223a2274657374222c2272223a2239333133313465363232633231613635227d" description:"" `
	DidVersionMetadata string `json:"didVersionMetadata" example:"0x7b226d657461223a2238313239336463633863623537623930663835383662396237343139333530653636316166623530663563633461656666376632643532373863623530353562227d" description:"" `
}

type TIRParams struct {
	From      string        `json:"from" example:"0x416e6e6162656c2e4c65652e452d412d506f652e" description:"" `
	Did       string        `json:"did" example:"did:ebsi:0xC3EF569B74B8DE63826aD256290E35BD35bD06c4" description:"" `
	Attribute *TIRAttribute `json:"attribute" description:"" `
}

type TIRAttribute struct {
	Body string `json:"body" example:"" description:"" `
	Hash string `json:"hash" example:"" description:"" `
}

type HelperFunctionResult struct {
	JsonRPC string               `json:"jsonrpc" example:"2.0" description:"" `
	Result  *UnsignedTransaction `json:"result" `
	Id      int                  `json:"id" example:"1" description:""`
}

type SignedTransaction struct {
	JsonRPC string           `json:"jsonrpc" example:"2.0" description:"" `
	Method  string           `json:"method" example:"insertDidDocument" description:""`
	Id      int              `json:"id" example:"1" description:""`
	Params  []SignedTxParams `json:"params" `
}

type SignedTxParams struct {
	Protocol             string               `json:"protocol" example:"eth" description:"eth or fabric" `
	UnsignedTransaction  *UnsignedTransaction `json:"unsignedTransaction" description:"" `
	R                    string               `json:"r" example:"0x..." description:"" `
	S                    string               `json:"s" example:"0x..." description:"" `
	V                    string               `json:"v" example:"0x..." description:"" `
	SignedRawTransaction string               `json:"signedRawTransaction" example:"--- signed raw transaction ---" description:"" `
}

type UnsignedTransaction struct {
	From     string `json:"from" example:"0x416e6e6162656c2e4c65652e452d412d506f652e" description:"" `
	To       string `json:"to" example:"0xFde86148db58f57787C06BeAf63a9c3f789357b3" description:"" `
	Data     string `json:"data" example:"0x0000..." description:"" `
	Nonce    string `json:"nonce" example:"0x00" description:"" `
	ChainId  string `json:"chainId" example:"0x1b3b" description:"" `
	GasLimit string `json:"gasLimit" example:"0x10000" description:"" `
	GasPrice string `json:"gasPrice" example:"0x00" description:"" `
	Value    string `json:"value" example:"0x00" description:"" `
}
